mapper.select:
select ds_no, ds_title, ds_content, ds_depart, ds_hit, ds_files, ds_regdate
from (
         select ds_no, ds_title, ds_content, ds_depart, ds_hit, ds_files, ds_regdate,
                row_number() over (order by ds_no desc) as rn
         from /*+ INDEX_DESC(tbl_dongnews SYS_C007141) */
             tbl_dongnews

     )
where rn between (#{pageNum} - 1) * #{amount} + 1 and #{pageNum} * #{amount}
